mapper.select:
SELECT n.no , n.title , n.context , n.create_id , DATE_FORMAT(n.create_dt, "%Y-%m-%d %H:%m:%s") AS create_dt , DATE_FORMAT(n.update_dt, "%Y-%m-%d %H:%m:%s") AS update_dt FROM specification_db.notice as n
AND n.delete_dt IS NULL
LIMIT #{pageMaxCount} OFFSET #{currentPage};

mapper.insert:
INSERT INTO specification_db.notice (
    title
    , context
    , create_id
    , create_dt)
VALUES(
        #{title}
       , #{context}
       , #{createID}
       , DATE_FORMAT(now(), "%Y-%m-%d %H:%m:%s")
);
